  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `school` varchar(255) DEFAULT NULL COMMENT '学校',
  `github` varchar(255) DEFAULT NULL,
  `visited_num` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户信息';

